import React, {Component} from 'react';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import Navigation from './components/Navigation/Navigation';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import './App.css';

const app = new Clarifai.App({
	apiKey: 'b08526772edf4dd5a35b5664b40b16cb'
});

const particlesOptions = {
  particles: {
    line_linked: {
      shadow: {
        enable: true,
        color: "#3CA9D1",
        blur: 5
      }
    }
  }
}
class App extends Component {
	// we need to detect what the user enters in the ImageLinkForm
	// so we need to create state so that our app knows what the value is the the user enters, remembers it and updates it every time it get changed.
	// in order to do that we will define a constructor, within the constructor we call super() to be able to use "this"
	constructor() {
		super();
		this.state = {
			input: '',
		}
	}

	onInputChange = (event) => {
		console.log(event.target.value);
	}

	onButtonSubmit = () => {
		console.log('click');
		app.models.predict(
			Clarifai.COLOR_MODEL, 
			"https://samples.clarifai.com/face-det.jpg")
		.then(
	    function(response) {
	      console.log(response);
	    },
	    function(err) {
	      // there was an error
	    }
  	);
	}

  render() {
    return (
      <div className="App">
        <Particles className='particles'
          params={particlesOptions}
        />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm 
	        onInputChange={this.onInputChange} 
	        onButtonSubmit={this.onButtonSubmit}
        />
        <FaceRecognition />
      </div>
    );
  }

}

export default App;
